glsl3.vert:
precision highp float;
uniform CCGlobal {
  mat4 cc_matView;
  mat4 cc_matViewInv;
  mat4 cc_matProj;
  mat4 cc_matProjInv;
  mat4 cc_matViewProj;
  mat4 cc_matViewProjInv;
  vec4 cc_cameraPos;
  vec4 cc_time;
  mediump vec4 cc_screenSize;
  mediump vec4 cc_screenScale;
};
uniform CCLocal {
  mat4 cc_matWorld;
  mat4 cc_matWorldIT;
};
in vec3 a_position;
in vec4 a_color;
out vec4 v_color;
#if USE_TEXTURE
in vec2 a_uv0;
out vec2 v_uv0;
#endif
void main () {
  vec4 pos = vec4(a_position, 1);
  #if CC_USE_MODEL
  pos = cc_matViewProj * cc_matWorld * pos;
  #else
  pos = cc_matViewProj * pos;
  #endif
  #if USE_TEXTURE
  v_uv0 = a_uv0;
  #endif
  v_color = a_color;
  gl_Position = pos;
}

glsl3.frag:
precision highp float;
#if USE_ALPHA_TEST
  uniform ALPHA_TEST {
    float alphaThreshold;
  };
#endif
uniform CCGlobal {
  mat4 cc_matView;
  mat4 cc_matViewInv;
  mat4 cc_matProj;
  mat4 cc_matProjInv;
  mat4 cc_matViewProj;
  mat4 cc_matViewProjInv;
  vec4 cc_cameraPos;
  vec4 cc_time;
  mediump vec4 cc_screenSize;
  mediump vec4 cc_screenScale;
};
in vec4 v_color;
#if USE_TEXTURE
in vec2 v_uv0;
uniform sampler2D texture;
#endif
float circle(in vec2 uv, in float radius){
  return 1.0 - smoothstep(radius-0.01*radius, radius+0.01*radius, length(uv));
}
const float PI =3.141592653;
void main () {
  vec4 o = vec4(0, 0, 0, 0);
  #if USE_TEXTURE
    vec2 uv = v_uv0 - 0.5;
    float R = 0.35;
    float radius = 0.05;
    vec2 pos = vec2(0.0);
    float rotation = 0.0;
    const float count = 8.0;
    float time = cc_time.x;
    for(float i = 0.0; i<count; i++){
        rotation = 2.0 * PI * i / count + 2.0 * PI * time / 5.0;
        pos = vec2(R*cos(rotation), R*sin(rotation));
        o.a += circle(uv-pos, radius * (sin(rotation*0.45)+1.0)/3.0 + radius/3.0);
        o.r += o.a * (sin(i/count + time + PI*6.0/3.0)+ 1.0)/2.0;
        o.g += o.a * (sin(i/count + time + PI*4.0/3.0)+1.0)/2.0;
        o.b += o.a * (sin(i/count + time + PI*2.0/3.0)+1.0)/2.0;
    }
  #endif
  gl_FragColor = o;
}

